import { RequiredLocaleConfig } from 'vuepress-shared';
import { LocaleConfig, PluginFunction } from '@vuepress/core';

interface BackToTopLocaleData {
    /**
     * Back to top button label text
     *
     * 返回顶部文字
     */
    backToTop: string;
}
type BackToTopLocaleConfig = RequiredLocaleConfig<BackToTopLocaleData>;

interface NoticeLocaleOptions {
    /**
     * Notice title
     *
     * 通知标题
     */
    title: string;
    /**
     * Notice content
     *
     * 通知内容
     */
    content: string;
    /**
     * Notice footer
     *
     * 通知操作
     */
    actions: {
        /**
         * Action text
         *
         * 操作文字
         */
        text: string;
        /**
         * Action link
         *
         * 操作链接
         */
        link?: string;
        /**
         * Action type
         *
         * 操作类型
         *
         * @default 'default
         */
        type?: "primary" | "default";
    }[];
}
interface NoticeOptions {
    /**
     * Notice locales Options
     *
     * Notice 多语言选项
     */
    locales: Record<string, NoticeLocaleOptions>;
    /**
     * Notice key
     *
     * @description Used to identify and store the notice status
     *
     * Notice 的 key
     *
     * @description 用于标识和存储 notice 的状态
     */
    key?: string;
    /**
     * Whether show notice only once or show it in every visit
     *
     * @description If `key` is not provided, this option will be ignored
     *
     * 是否只显示一次通知
     *
     * @description 如果没有提供 `key`，此选项将被忽略
     *
     * @default false
     */
    showOnce?: string;
    /**
     * Whether the notice shall be confirmed
     *
     * 通知是否需要确认
     *
     * @default false
     */
    confirm?: boolean;
    /**
     * Whether the notice should appear fullscreen
     *
     * 通知是否应该全屏显示
     *
     * @default false
     */
    fullscreen?: boolean;
}

declare const backToTopLocales: BackToTopLocaleConfig;

type AvailableComponent = "AudioPlayer" | "Badge" | "BiliBili" | "CodePen" | "FontIcon" | "PDF" | "StackBlitz" | "VideoPlayer" | "YouTube";
interface ComponentOptions {
    /**
     * Components to be registered
     *
     * 需要被注册的组件
     *
     * @default []
     */
    components?: AvailableComponent[];
    /**
     * Global config for components
     *
     * 组件全局配置
     */
    componentOptions?: {
        /**
         * FontIcon config
         *
         * 图标配置
         */
        fontIcon?: {
            /**
             * Link of font icon asset
             *
             * 字体图标资源链接
             *
             * @description `'iconfont'` and `'fontawesome'` keywords are supported
             */
            assets?: "iconfont" | "fontawesome" | `//${string}` | `http://${string}` | `https://${string}`;
            /**
             * Class prefix of font icon
             *
             * 字体图标的 Class 前缀
             *
             * @default ''
             */
            prefix?: string;
        };
        /**
         * PDF viewer config
         *
         * PDF 阅读器配置
         */
        pdf?: {
            /**
             * Location of pdfjs
             *
             * pdfjs 的位置
             */
            pdfjs?: string;
        };
    };
    rootComponents?: {
        /**
         * Add This 的公开 ID
         *
         * Public ID for add this
         */
        addThis?: string;
        /**
         * Whether enabling backToTop button
         *
         *
         * @description When setting a number, it will be used as backToTop button threshold distance (in pixels)
         *
         * 是否启用返回顶部按钮
         *
         * @description 当设置为数字时，将会作为返回顶部按钮距离阈值 (单位: 像素)
         */
        backToTop?: number | boolean;
        /**
         * Global notice config
         *
         * 全局通知配置
         */
        notice?: NoticeOptions;
    };
    locales?: {
        /**
         * backToTop button Locales config
         *
         * 返回顶部按钮国际化配置
         */
        backToTop?: LocaleConfig<BackToTopLocaleData>;
    };
}

declare const componentsPlugin: (options: ComponentOptions, legacy?: boolean) => PluginFunction;

export { AvailableComponent, BackToTopLocaleConfig, BackToTopLocaleData, ComponentOptions, NoticeLocaleOptions, NoticeOptions, backToTopLocales, componentsPlugin };
